export const userLogin = user => {
    let returnedUser = null
    console.log("User Action", user)
    fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
    })
        .then(response => response.json())
        .then(data => {
            returnedUser = data

        })
    return (dispatch) => {
        dispatch({
            type: "USER_LOGIN",
            user
        })
    }
}
