import React from "react"
import { Container } from 'semantic-ui-react';
import Login from "../forms/Login";
import { connect } from "react-redux"
import { userLogin } from "../actions/userActions"

import LoginAlert from "../semanticComponents/LoginAlert"



class Welcome extends React.Component {
    constructor() {
        super()
        this.state = {
            showLoginError: false
        }
    }

    handleLoginSubmit = (event) => {
        // console.log(event.target)
        const userEmail = event.target.querySelector("#emailInput").value
        const userPassword = event.target.querySelector("#passwordInput").value
        const userData = {
            email: userEmail,
            password: userPassword
        }
        fetch('http://localhost:3000/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
                data.status === 401 ? this.setState({ showLoginError: true }) : this.props.userLogin(data)
            })
        event.target.reset()

    }


    render() {
        return (
            <Container>
                {this.state.showLoginError && <LoginAlert />}
                <Login handleLoginSubmit={this.handleLoginSubmit} />
            </Container>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return { userLogin: (userData) => dispatch(userLogin(userData)) }
}


export default connect(null, mapDispatchToProps)(Welcome)
