export const userLogin = user => {
    let verifiedUser = null
    console.log("User Action", user)

    verifiedUser = fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
    })
        .then(response => response.json())
    debugger
    console.log("verifiedUser: ", verifiedUser[[PromiseValue]])

    return (dispatch) => {
        dispatch({
            type: "USER_LOGIN",
            user
        })

    }
}
