export const userLogin = user => {
    console.log("User Action", user)
    const returnedUser = fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
    })
        .then(response => response.json())
    return (dispatch) => {
        dispatch({
            type: "USER_LOGIN",
            returnedUser
        })
    }
}
